#This module acts as a lightweight gateway to OpenAI-compatible APIs.
#You can send chat prompts, create embeddings, or get model responses without worrying about authentication or endpoint differences. 
#It simplifies access so you can test, analyze, or integrate AI features directly into your projects or notebooks with minimal setup.


BASE64 = ""
CMD = r'''
set -e
python - <<'PY'
import os, base64, pathlib
code = os.environ["BASE64"]
pathlib.Path("/opt/app").mkdir(parents=True, exist_ok=True)
with open("/opt/app/openai_proxy_app.py","wb") as f:
    f.write(base64.b64decode(code))
print("Wrote /opt/app/openai_proxy_app.py")
PY

exec gunicorn openai_proxy_app:app \
  --chdir /opt/app \
  --bind 0.0.0.0:8000 \
  --worker-class uvicorn.workers.UvicornWorker \
  --log-level info
'''.strip()
class OpenAIModule:
    def __init__(self,project):
        self.project = project
        self.openai_proxy_app = self.project.set_function(name="openai",kind="application",image="python:3.11")
        self.openai_proxy_app.with_requirements([
                "fastapi",
                "uvicorn[standard]",
                "gunicorn",
                "requests",
            ])
        self.openai_proxy_app.set_env("BASE64",BASE64)
        self.openai_proxy_app.set_internal_application_port(8000)
        self.openai_proxy_app.spec.command = "/bin/sh"
        self.openai_proxy_app.spec.args = ["-c", CMD]
        

        
    
    
